import { useState } from "preact/hooks"

const BasicVariables = () => {
  const [count, setCount] = useState(0)

  function handleClick() {
    setCount(count + 1)
  }
  return (
    <button onClick={handleClick}>
      Count: {count}
    </button>
  )
}

export default BasicVariables

preact.ConditionalRendering:
import { h } from "preact"
import { useState } from "preact/hooks"

const ConditionalRendering = () => {
  const [x] = useState(Math.floor(Math.random() * 15))

  return (
    <>
      {x > 10 ? (
        <p>{x} is greater than 10</p>
      ) : 5 > x ? (
        <p>{x} is less than 5</p>
      ) : (
        <p>{x} is between 5 and 10</p>
      )}
    </>
  )
}

export default ConditionalRendering

preact.DependentVariables:
import { h } from "preact"
import { useEffect, useState } from "preact/hooks"

const DependentVariables = () => {
  const [count, setCount] = useState(1)
  const [doubled, setDoubled] = useState(count * 2)
  const [quadrupled, setQuadrupled] = useState(doubled * 2)

  useEffect(() => {
    setDoubled(count * 2)
    setQuadrupled(doubled * 2)
  }, [count, doubled])

  function handleClick() {
    setCount(count + 1)
  }

  return (
    <>
      <button onClick={handleClick}>Count: {count}</button>
      <p>
        {count} * 2 = {doubled}
      </p>
      <p>
        {doubled} * 2 = {quadrupled}
      </p>
    </>
  )
}

export default DependentVariables

preact.ListLooping:
import { h } from "preact"

const cats = [
  { id: "J---aiyznGQ", name: "Keyboard Cat" },
  { id: "z_AbfPXTKms", name: "Maru" },
  { id: "OUtn3pvWmpg", name: "Henri The Existential Cat" },
]

const ListLooping = () => {
  return (
    <>
      <h1>The Famous Cats of YouTube</h1>
      <ul>
        {cats.map(({ id, name }, i) => (
          <li key={id}>
            <a href={`https://www.youtube.com/watch?v=${id}`}>
              <a target="_blank">
                {i + 1}: {name}
              </a>
            </a>
          </li>
        ))}
      </ul>
    </>
  )
}

export default ListLooping

