react.BasicVariables:
import { useState } from "react";

const BasicVariables = () => {
  const [count, setCount] = useState(0);

  function handleClick() {
    setCount(count + 1);
  }
  return (
    <button onClick={handleClick}>
      Clicked {count} {count === 1 ? "time" : "times"}
    </button>
  );
};

export default BasicVariables;

react.ConditionalRendering:
import { useState } from "react";

const ConditionalRendering = () => {
  const [x] = useState(Math.floor(Math.random() * 15));

  return (
    <>
      {x > 10 ? (
        <p>{x} is greater than 10</p>
      ) : 5 > x ? (
        <p>{x} is less than 5</p>
      ) : (
        <p>{x} is between 5 and 10</p>
      )}
    </>
  );
};

export default ConditionalRendering;

react.DependentVariables:
import { useEffect, useState } from "react";

const DependentVariables = () => {
  const [count, setCount] = useState(1);
  const [doubled, setDoubled] = useState(count * 2);
  const [quadrupled, setQuadrupled] = useState(doubled * 2);

  useEffect(() => {
    setDoubled(count * 2);
    setQuadrupled(doubled * 2);
  }, [count, doubled]);

  function handleClick() {
    setCount(count + 1);
  }

  return (
    <>
      <button onClick={handleClick}>Count: {count}</button>
      <p>
        {count} * 2 = {doubled}
      </p>
      <p>
        {doubled} * 2 = {quadrupled}
      </p>
    </>
  );
};

export default DependentVariables;

